void ntt(int16_t b[NTRUPLUS_N], const int16_t a[NTRUPLUS_N])
{
	int16_t t1,t2,t3,t4;
	int16_t zeta1, zeta2;
	int k = 0;

	zeta1 = zetas[k++];

	for(int i = 0; i < NTRUPLUS_N/2; i++)
	{
		t1 = fqmul(zeta1, a[i + NTRUPLUS_N/2]);

		b[i + NTRUPLUS_N/2] = (a[i] + a[i + NTRUPLUS_N/2] - t1);
		b[i      ] = (a[i] + t1);
	}

	for(int step = NTRUPLUS_N/6; step >= 64; step = step/3)
	{
		for(int start = 0; start < NTRUPLUS_N; start += 3*step)
		{
			zeta1 = zetas[k++];
			zeta2 = zetas[k++];

			for(int i = start; i < start + step; i++)
			{
				t1 = fqmul(zeta1, b[i + step]);
				t2 = fqmul(zeta2, b[i + 2*step]);
				t3 = fqmul(2571, t1);
				t4 = fqmul(1033, t2);

				t1 = t1 + t2;
				t3 = t3 + t4;

				b[i + 2*step] = fqred16(b[i] - (t1 + t3));
				b[i + step] = fqred16(b[i] + t3);
				b[i    ] = fqred16(b[i] + t1);
			}
		}
	}

	for(int step = 32; step >= 2; step >>= 1)
	{
		for(int start = 0; start < NTRUPLUS_N; start += (step << 1))
		{
			zeta1 = zetas[k++];

			for(int i = start; i < start + step; i++)
			{
				t1 = fqmul(zeta1, b[i + step]);
				
				b[i + step] = fqred16(b[i] - t1);
				b[i       ] = fqred16(b[i] + t1);
			}
		}
	}
}

void invntt(int16_t r[NTRUPLUS_N], const int16_t a[NTRUPLUS_N])
{
	int16_t t1,t2,t3,t4;
	int16_t zeta1,zeta2;
	int k = 0;

	for(int i = 0; i < NTRUPLUS_N; i++)
	{
		r[i] = a[i];
	}

	for(int step = 2; step <= 32; step <<= 1)
	{
		for(int start = 0; start < NTRUPLUS_N; start += (step << 1))
		{
			zeta1 = zetas_inv[k++];

			for(int i = start; i < start + step; i++)
			{
				t1 = r[i + step];

				r[i + step] = fqred16(fqmul(zeta1, r[i] - t1));
				r[i       ] = fqred16(r[i] + t1);
			}
		}
	}

	for(int step = 64; step <= NTRUPLUS_N/6; step = 3*step)
	{
		for(int start = 0; start < NTRUPLUS_N; start += 3*step)
		{
			zeta1 = zetas_inv[k++];
			zeta2 = zetas_inv[k++];

			for(int i = start; i < start + step; i++)
			{
				t1 = fqred16(fqmul(1033, r[i + step]) + fqmul(2571, r[i + 2*step]));
				t2 = fqred16(r[i + step] + r[i + 2*step]);

				r[i + 2*step] = fqred16(fqmul(zeta2, r[i] - (t1 + t2)));
				r[i + step] = fqred16(fqmul(zeta1, r[i] + t1));
				r[i    ] = fqred16(r[i] + t2);
			}
		}
	}

	zeta1 = zetas_inv[k];

	for(int i = 0; i < NTRUPLUS_N/2; i++)
	{
		t1 = fqred16(r[i] + r[i + NTRUPLUS_N/2]);
		t2 = fqmul(zeta1, r[i] - r[i + NTRUPLUS_N/2]);

		r[i      ] = fqred16(fqmul(1712, t1 - t2));
		r[i + 576] = fqred16(fqmul(3424, t2));	
	}
}

void basemul(int16_t c[2], const int16_t a[2], const int16_t b[2], int16_t zeta)
{
	int16_t t1 = fqmul(a[0], b[0]);
	int16_t t2 = fqmul(a[1], b[1]);
	int16_t t3 = fqmul((a[0] + a[1]),(b[0] + b[1]));

	c[1] = t3 - t1 - t2;
	c[0] = fqmul(t2, zeta);
	c[0] += t1;
}

int baseinv(int16_t b[2], const int16_t a[2], int16_t zeta)
{
	int16_t det, t;
	int r;

	det = fqmul(a[0], a[0]);
	t = fqmul(a[1], a[1]);
	t = fqmul(t, zeta);
	det = det - t;

	det   = fqinv(det);
	b[0] = fqmul(a[0], det);
	b[1] = fqmul(-a[1], det);

	r = (uint16_t)det;
	r = (uint32_t)(-r) >> 31;
	return r - 1;
}